examples.code:
// Syntax
import { HashSet, pipe } from "effect"

// with data-last, a.k.a. pipeable API
pipe(
HashSet.make(1, 2, 3),
HashSet.mutate((set) => {
HashSet.add(set, 4)
HashSet.remove(set, 1)
})
)

// or piped with the pipe function
HashSet.make(1, 2, 3).pipe(
HashSet.mutate((set) => {
HashSet.add(set, 4)
HashSet.remove(set, 1)
})
)

// or with data-first API
HashSet.mutate(HashSet.make(1, 2, 3), (set) => {
HashSet.add(set, 4)
HashSet.remove(set, 1)
})
