import { Effect } from "effect"

const tasks = [
Effect.succeed(1),
Effect.fail("Error 1"),
Effect.succeed(2),
Effect.fail("Error 2")
]

const program = Effect.gen(function*() {
const successfulResults = yield* Effect.allSuccesses(tasks)
console.log(successfulResults)
})

Effect.runFork(program)
// Output: [1, 2]
